import {
    Tag,
    Col,
    Icon,
    Cell,
    CellGroup,
    Swipe,
    Toast,
    SwipeItem,
    GoodsAction,
    GoodsActionBigBtn,
    GoodsActionMiniBtn,
    Dialog,
    Picker,
    Popup
} from 'vant';
import storeData from './store/index';
import ajaxUser from '@/api/user';
import ajax from '@/api/login';
import Cookies from 'js-cookie';
import RSA from '@/libs/RSA';

export default {
    components: {
        [Tag.name]: Tag,
        [Col.name]: Col,
        [Icon.name]: Icon,
        [Cell.name]: Cell,
        [CellGroup.name]: CellGroup,
        [Swipe.name]: Swipe,
        [SwipeItem.name]: SwipeItem,
        [GoodsAction.name]: GoodsAction,
        [GoodsActionBigBtn.name]: GoodsActionBigBtn,
        [GoodsActionMiniBtn.name]: GoodsActionMiniBtn,
        Dialog: Dialog,
        [Picker.name]: Picker,
        Popup: Popup
    },

    data() {
        return Object.assign(storeData.call(this), {
            // indexPicker:  0,
            // valuePicker: '',
            departNameShow: false,
            merchantName: '',
            merchantId: ''
        });
    },
    created() {
        this.getToken();
        this.accountList = this.accountListComputed();
        this.departList = this.departListComputed();

        if (localStorage.getItem('merchantName') && localStorage.getItem('merchantName').length > 0) {
            this.merchantName = localStorage.getItem('merchantName');
            this.merchantId = localStorage.getItem('merchantId');
            this.departPickerShow = true;
        } else {
           this.departPickerShow =false;
        }

        if (this.departList && this.departList.length > 0) {
            this.departListCurrent = this.departList[0]['depart'];
            this.depart = this.departListCurrent;
            this.valuePicker = this.departListCurrent;
        }

        if (this.departList) {
            let arr = [];
            this.departList.forEach(it => {
                arr.push(it.depart);
            });
            this.departListColumns = arr;
        }
        // if (window.history && window.history.pushState) {
        //     window.onpopstate = function () {
        //         window.history.forward(1);
        //     }
        // }
    },
    watch: {
        'depart': function (val, old) {
            if (val) {
                this.loginRule[0] = 1;
                if (this.account && this.password) {
                    this.loginStatus = true;
                } else {
                    this.loginStatus = false;
                }
            } else {
                this.loginRule[0] = 0;
                this.loginStatus = false;
            }
            if (val !== old) {
                this.loginInputStatus[0] = 1;
                this.loginInputStatus[1] = 0;
                this.loginInputStatus[2] = 0;
            } else {
                this.loginInputStatus[0] = 0;
            }
        },
        'account': function (val, old) {
            if (val) {
                this.loginRule[1] = 1;
                if (this.account && this.password) {
                    this.loginStatus = true;
                } else {
                    this.loginStatus = false;
                }
            } else {
                this.loginRule[1] = 0;
                this.loginStatus = false;
            }
            if (val !== old) {
                this.loginInputStatus[0] = 0;
                this.loginInputStatus[1] = 1;
                this.loginInputStatus[2] = 0;
                this.accountShow = false;
            } else {
                this.loginInputStatus[1] = 0;
            }
        },
        'password': function (val, old) {
            if (val) {
                this.loginRule[2] = 1;
                if (this.account && this.password) {
                    this.loginStatus = true;
                } else {
                    this.loginStatus = false;
                }
            } else {
                this.loginRule[2] = 0;
                this.loginStatus = false;
            }
            if (val !== old) {
                this.loginInputStatus[0] = 0;
                this.loginInputStatus[1] = 0;
                this.loginInputStatus[2] = 1;
            } else {
                this.loginInputStatus[2] = 0;
            }
        },
        accountList() {
            this.accountListComputed();
        },
        departList() {
            this.departListComputed();
        },
    },
    methods: {
        getToken() {
            ajax.getToken({
                _: new Date().getTime()
            }).then(response => {
                if (!response.success === true) {
                    return;
                }
                let res = response.data;
                const keyPair = RSA.getKeyPair(
                    res.exponent, '', res.modulus
                );
                this.keyPair = keyPair;
            }).catch(() => {
            });
        },
        loginAction() {
            if (this.loginRule.join() === '1,1,1') {
                this.loginStatus = true;
                this.loginRuleTextStatus = false;
                this.loginFetch();
            } else {
                this.loginInputStatus = [0, 0, 0];
                this.loginStatus = false;
                this.loginRuleTextStatus = true;
            }
        },
        loginFetch() {
            ajax.login({
                clientMerchantId: this.depart,
                login_name: this.account,
                login_pwd: RSA.encryptedString(this.keyPair, this.password)
            }).then(response => {
                if (!response.success === true) {
                    Dialog.confirm({
                        title: response.msg || '登录失败',
                        message: ''
                    }).then(() => {
                    }).catch(() => {
                    });
                    localStorage.setItem('departName_current', '');
                    localStorage.setItem('userName_current', '');
                    return;
                }
                localStorage.setItem('departName_current', this.depart);
                localStorage.setItem('userName_current', this.account);
                localStorage.setItem('accountList', JSON.stringify(this.accountListComputed()));
                localStorage.setItem('departList', JSON.stringify(this.departListComputed()));
                this.merchantInfo();

                this.$router.push({
                    name: 'home'
                });
            }).catch(() => {
            });
        },
        merchantInfo() {
            ajaxUser.merchantUser({}).then(response => {
                if (!response.success === true) {
                    return;
                } else {
                    if (response.data && response.data) {
                        this.merchantName = response.data.merchantNickName || '';
                        localStorage.setItem('merchantName', this.merchantName);
                        localStorage.setItem('merchantId', this.depart);
                    }
                }
            }).catch(() => {
            });
        },
        accountSelectHandle() {
            this.accountShow = !this.accountShow;
            if (this.accountList) {

            }
        },
        accountChangeHandle(item) {
            this.account = item.account;
            this.accountShow = false;
        },
        passwordShowHandle() {
            this.passwordShow = !this.passwordShow;
        },
        deleteAccountAction(item, key) {
            Dialog.confirm({
                title: '确认删除历史登录账号?',
                message: item.account
            }).then(() => {
                if (item.account === this.account) {
                    this.account = '';
                }
                if (key === 0) {
                    localStorage.setItem('userName_current', '');
                }
                this.accountList.splice(this.accountList.findIndex(v => v === item), 1);
                localStorage.setItem('accountList', JSON.stringify(this.accountList));

                if (this.accountList.length === 0) {
                    this.accountShow = false;
                }
            }).catch(() => {
                this.accountShow = true;
            });
        },
        accountListComputed() {
            let userListOld;
            if (localStorage.getItem('accountList')) {
                userListOld = eval(localStorage.getItem('accountList')) || [];
            } else {
                userListOld = [];
            }
            let userList;
            if (localStorage.getItem('userName_current')) {
                if (userListOld) {
                    userList = [{account: localStorage.getItem('userName_current') || this.accountName}].concat(userListOld.filter((it) => {
                        if (it.account !== localStorage.getItem('userName_current') || this.accountName) {
                            return it;
                        }
                    }));
                } else {
                    userList = [{account: localStorage.getItem('userName_current') || this.accountName}];
                }
            } else {
                userList = userListOld;
            }
            return userList;
        },
        departListComputed(status, index) {
            let departListOld;

            if (localStorage.getItem('departList')) {
                departListOld = eval(localStorage.getItem('departList')) || [];
            } else {
                departListOld = [];
            }
            let departList;
            if (localStorage.getItem('departName_current')) {
                if (departListOld) {
                    departList = [{depart: localStorage.getItem('departName_current') || this.depart}].concat(departListOld.filter((it) => {
                        if (it.depart !== localStorage.getItem('departName_current') || this.depart) {
                            return it;
                        }
                    }));
                } else {
                    departList = [{depart: localStorage.getItem('departName_current') || this.depart}];
                }
            } else {
                departList = departListOld;
            }
            return departList;
        },
        departPickerHandle() {
            this.departPickerShow = !this.departPickerShow;
            if (this.departPickerShow) {
                this.loginInputStatus[0] = 0;
                this.loginInputStatus[2] = 0;
            }

            this.depart = this.merchantId;

        },
        onChange(picker, value, index) {
            // Toast(`当前值：${value}, 当前索引：${index}`);
            // this['picker'] = picker;
        },
        onConfirm(value, index) {
            if (this.depart !== value) {
                this.departListCurrent = value;
                this.depart = value;
                let list = [value].concat(this.departListColumns);
                list.splice(index + 1, 1);
                this.departListColumns = list;
            }

            setTimeout(() => {
                this.departPickerShow = false;
            }, 800);
            // this.$toast(value, index);
        },
        onCancel() {
            // this.$toast('取消');
            this.departPickerShow = false;
        }
    },
    // beforeDestroy: {
    //
    // }
};
